// CartContext.js
import React, { createContext, useContext, useReducer } from 'react';

const CartContext = createContext();

const initialState = {
  cartItems: [], // Initialize with your initial cart items
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      // Implement logic to add an item to the cart
      return { ...state, cartItems: [...state.cartItems, action.payload] };
    case 'REMOVE_FROM_CART':
      // Implement logic to remove an item from the cart
      return { ...state, cartItems: state.cartItems.filter(item => item.id !== action.payload) };
    case 'UPDATE_CART_ITEM':
      // Implement logic to update the quantity of an item in the cart
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item.id === action.payload.id ? { ...item, count: action.payload.count } : item
        ),
      };
    // Add more cases for other actions if needed
    default:
      return state;
  }
};

const CartProvider = ({ children }) => {
  const [cartState, dispatch] = useReducer(cartReducer, initialState);

  const addToCart = (item) => {
    dispatch({ type: 'ADD_TO_CART', payload: item });
  };

  const removeFromCart = (itemId) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: itemId });
  };

  const updateCartItem = (itemId, count) => {
    dispatch({ type: 'UPDATE_CART_ITEM', payload: { id: itemId, count } });
  };

  return (
    <CartContext.Provider value={{ cartState, addToCart, removeFromCart, updateCartItem }}>
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export { CartProvider, useCart };
