import React, {useState, useEffect} from 'react'
import axios from "axios";
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { redirect } from 'react-router-dom';

const Create = () => {

   // const [show, setShow] = useState(false);

    // const handleClose = () => setShow(false);
    // const handleShow = () => setShow(true);

    const [categoryName, setCategoryName] = useState('')
    const [order,setOrder] = useState('')

    const [data,setData] = useState({
        categoryName:'',
        order:''
    })

    useEffect(() => {
        getData();
    }, [])

    const getData = () =>
    {
        axios.get('http://localhost:5120/api/Category')
        .then((result)=>{
            setData(result.data)
        })
        .catch((error)=>{
            console.log(error)
        })
    }

    const handleSave = () => {
        const url = 'http://localhost:5120/api/Category';
        const data = {
           
            "categoryName": categoryName,
            "order": order
          }
          axios.post(url, data)
          .then((result) =>{
            getData();
            clear();
            toast.success('Category has been added');
          })
          .catch((error)=>{
            toast.error(error);
          })
    }
    const clear = () => {
        setCategoryName('');
        setOrder('');
    }

    const [formData, setFormData] = useState({
        name: '',
        number:''
    });

    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
            
        });
        const{categoryName,order, value1, value2} = e.target;
        setData({
            ...data,
            [categoryName]: value1,
            [order]: value2
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const newErrors = {};
        const numberRegex = /^\d{2}$/; // Adjust as needed

        if (!formData.name || !/^[a-zA-Z\s]+$/.test(formData.name)) {
            newErrors.name = 'Category name is required';
        }

        if (!formData.number || !numberRegex.test(formData.number)) {
            newErrors.number = 'Order quantity is required and should not be more than 100';

           

           
            // if(pattern>=99)
            // {
            //     newErrors.number = 'number quantity cannot be greater than 100';
            // }
            // else{
            //     newErrors.number = '';
            // }

        }
        
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };

  return (
    <div className='container'>
      <div className="card shadow bnumber-0 mt-4">
    <div className="card-header bg-info bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-white py-2">Create Category</h2>
            </div>
        </div>
    </div> 

    <div className="card-body p-4">
    <form method="post" className="row" onSubmit={handleSubmit}>
        <ToastContainer></ToastContainer>
    <div className="bnumber p-3">

        <div className="form-floating py-2 col-12">
                    <input type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                
                                className={`form-control ${errors.name ? 'is-invalid' : ''}`} />
                    <label className="ms-2">Category Name</label>
   
                    <span className="text-danger">{errors.name}</span> 
        </div>

                
                <div className="form-floating py-2 col-12">
                   
                    <input type="number"
                                name="number" pattern="[0-9]{2}"
                                value={formData.number}
                                onChange={handleInputChange}
                                className={`form-control ${errors.number ? 'is-invalid' : ''}`}  />
                   
                      <label className="ms-2">Displaynumber</label> 
     
                      <span className="text-danger">{errors.number}</span>
        </div>

        <div className="row pt-3">
        <div className="text-center">
        <a href='/category'><button type="submit"  className="btn btn-dark mx-2" style={{width:"150px"}} onClick={()=>handleSave()}>Create</button></a>
        <a className="btn btn-secondary bnumber-secondary" href='/category'>
            Back To List?
        </a>
        </div>
    </div>
    </div>
</form>
    </div>
</div>
    </div>
  )
};

  export default Create;
