import React, { useState } from 'react';
const RegisterForm = () => {
    const [formData, setFormData] = useState({
        email: '',
        name: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        select:''
    });
    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        // Client-side validation logic
        const newErrors = {};
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const phoneRegex = /^\d{10}$/; // Adjust as needed
        const passwordRegex = /(?=.*[A-Z])(?=.*\d)(?=.*\W)/;
        // const selectRegex= '';
        if (!formData.email || !emailRegex.test(formData.email)) {
            newErrors.email = 'Valid email is required';
        }
        if (!formData.name || !/^[a-zA-Z\s]+$/.test(formData.name)) {
            newErrors.name = 'Valid name is required';
        }
        if (!formData.phoneNumber || !phoneRegex.test(formData.phoneNumber)) {
            newErrors.phoneNumber = 'Valid phone number is required';
        }
        if (!formData.password || !passwordRegex.test(formData.password)) {
            newErrors.password =
                'Password must contain at least one uppercase letter, one digit, and one special character';
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }
        // if (!formData.select || !selectRegex.test(formData.select)) {
        //     newErrors.select =  'Select the Role';
               
        // }
        if (!formData.select) {
            newErrors.select =  'Select the Role';
               
        }
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };
    return (
        <div className='container'>
            <div class="card shadow border-0 mt-4">
                <div class="card-header bg-dark bg-gradient mt-0 py-4">
                    <div class="row ">
                        <div class="col-12 text-center ">
                            <h2 class="py-2 text-light">Register</h2>
                        </div>
                    </div>
                </div>
            </div>
            <div className="card-body p-4">
                <div className="row pt-3">
                    <div className="col-md-12"></div>
                    <form className="row" method="post" onSubmit={handleSubmit}>
                        {/* ... other form elements */}
                        <div className="form-floating mb-3 col-md-12">
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                                aria-required="true"
                            />
                            <label htmlFor="email" className="ms-2 text-muted">
                                Email
                            </label>
                            <span className="text-danger">{errors.email}</span>
                        </div>
                        <div className="form-floating mb-3 col-md-6">
                            <input
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                            />
                            <label htmlFor="name" className="ms-2 text-muted">
                                Name
                            </label>
                            <span className="text-danger">{errors.name}</span>
                        </div>
                        <div className="form-floating mb-3 col-md-6">
                            <input
                                type="tel"
                                name="phoneNumber"
                                value={formData.phoneNumber}
                                onChange={handleInputChange}
                                className={`form-control ${errors.phoneNumber ? 'is-invalid' : ''}`}
                            />
                            <label htmlFor="phoneNumber" className="ms-2 text-muted">
                                Phone No
                            </label>
                            <span className="text-danger">{errors.phoneNumber}</span>
                        </div>
                        <div className="form-floating mb-3 col-md-6">
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                            />
                            <label htmlFor="password" className="ms-2 text-muted">
                                Password
                            </label>
                            <span className="text-danger">{errors.password}</span>
                        </div>
                        <div className="form-floating mb-3 col-md-6">
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                onChange={handleInputChange}
                                className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                            />
                            <label htmlFor="confirmPassword" className="ms-2 text-muted">
                                Confirm Password
                            </label>
                            <span className="text-danger">{errors.confirmPassword}</span>
                        </div>
                        <div className="form-floating mb-3 col-md-6">
                            {/* <input
                                type="select"
                                name="select"
                                value={formData.select}
                                onChange={handleInputChange}
                                className={`form-control ${errors.select ? 'is-invalid' : ''}`}
                            /> */}
                            <select  type="select"
                                name="select"
                                value={formData.select}
                                onChange={handleInputChange}
                                className={`form-control ${errors.select ? 'is-invalid' : ''}`}>a</select>
                            <label htmlFor="select" className="ms-2 text-muted">
                                select role
                            </label>
                            <span className="text-danger">{errors.select}</span>
                        </div>
                        {/* <div className="form-floating mb-3 col-md-6">
                            <select className = {`form-select ${errors.select ? 'is-invalid' : ''}`} name='select' htmlFor="select" value={formData.select}
                                onChange={handleInputChange}>
                                <option>-Select Role-</option>
                                <span className="text-danger">{errors.confirmPassword}</span>
                            </select>
                        </div> */}
                        <div class="col-12">
                            <button id="registerSubmit" type="submit" class="w-100 btn btn-dark mb-3">Register</button>
                        </div>
                    </form>
                    {/* ... rest of the component code */}
                </div>
            </div>
        </div>


    );
};
export default RegisterForm;
