// Cart.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from './CartContext';

const Cart = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [productData, setProductData] = useState(null);
  const [count, setCount] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (id) {
          const response = await axios.get(`http://localhost:5120/api/Product/${id}`);
          const productData = response.data;
          const imageUrl = productData.imageUrl.split('\\').pop();
          setProductData({ ...productData, imageUrl });
        }
      } catch (error) {
        console.error('Error fetching product data:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleCountChange = (e) => {
    const newCount = Math.max(1, parseInt(e.target.value, 10) || 1);
    setCount(newCount);
  };

  const handleAddToCart = () => {
    if (productData) {
      addToCart({
        id: productData.id,
        product: productData,
        count,
      });
      console.log('Item added to cart:', productData);
      navigate('/cart');
    }
  };

  return (
    <div>
      {productData ? (
        <form method="post">
          {/* ... (your existing code for product details) */}
          <div className="row">
            <div className="col-12 col-md-6 pb-1">
              <button
                type="button"
                onClick={handleAddToCart}
                className="btn btn-dark bg-gradient w-100 py-2 text-uppercase fw-semibold"
              >
                Add to Cart
              </button>
            </div>
          </div>
        </form>
      ) : (
        <p>Loading...</p>
      )}

      {/* Cart Section */}
      <div>
        <h2>Shopping Cart</h2>
        {/* Display Cart Items */}
        {/* Assuming you have access to cart items in the same component */}
        {/* Adjust this part based on your actual cart data structure */}
        {cartItems.map((item) => (
          <div key={item.id}>
            <h3>{item.product.title}</h3>
            <p>{item.product.description}</p>
            <p>Quantity: {item.count}</p>
            <button onClick={() => handleRemove(item.id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Cart;
