import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Details = () => {
  const { id } = useParams();
  const [productData, setProductData] = useState(null);

  useEffect(() => {
    // Ensure id is available and not an empty string
    if (id) {
      axios.get(`http://localhost:5120/api/Product/${id}`)
        .then((response) => {
          setProductData(response.data);
        })
        .catch((error) => {
          console.error('Error fetching product data:', error);
        });
    }
  }, [id]);

  return (
    <div>
      {productData ? (
        <form method="post">
          <div className="card shadow border-0 mt-4 mb-4">
            <div className="card-header bg-secondary bg-gradient text-light py-4">
              <div className="row">
                <div className="col-12 text-center">
                  <h3 className="text-white text-uppercase">{productData.title}</h3>
                  <p className="text-white-50 fw-semibold mb-0">by {productData.author}</p>
                </div>
              </div>
            </div>
            <div className="card-body">
              {/* Add your UI elements to display product details */}
              {/* Example: */}
              <div className="row">
                <div className="col-12 col-lg-3 offset-lg-1 text-center mb-3">
                  {/* Add your carousel or image display component here */}
                </div>
                <div className="col-12 col-lg-6 offset-lg-1">
                  {/* Add your product details display here */}
                  <div className="col-12 col-md-6 pb-4">
                    <span className="badge">{productData.category}</span>
                  </div>
                  <div className="row ps-2">
                    <h6 className="text-dark text-opacity-50 ">ISBN: {productData.isbn}</h6>
                  </div>
                  {/* Add more details based on your data */}
                </div>
              </div>
              {/* Add your additional UI elements and form fields as needed */}
            </div>
          </div>
        </form>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Details;
