import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

 const EditP = () => {
    const navigate = useNavigate();
    const { id } = useParams();

    const[data,setData] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [isbn, setISBN] = useState('');
    const [listPrice, setListPrice] = useState('');
    const [author, setAuthor] = useState('');
    const [price, setPrice] = useState('');
    const [price50, setPrice50] = useState('');
    const [price100, setPrice100] = useState('');
    const [select, setSelect] = useState('');
    const [imageUrl,setImageUrl] = useState('');

    useEffect(() => {
        axios
          .get(`http://localhost:5120/api/Product/${id}`)
          .then((result) => {
            const { title, description, author, isbn, listPrice, price,price50,price100,select,imageUrl} = result.data;
            setTitle(title);
            setDescription(description);
            setISBN(isbn);
            setAuthor(author);
            setListPrice(listPrice);
            setPrice(price);
            setPrice50(price50);
            setPrice100(price100);
            setSelect(select);
            setImageUrl(imageUrl);
          })
          .catch((error) => {
            console.log(error);
          });
      }, [id]);
     
      useEffect(() => {
        getData();
    }, [])

    const getData = () =>
{
    axios.get('http://localhost:5120/api/Product')
    .then((result)=>{
        setData(result.data)
       // toast.success('Category has been added')
    })
    .catch((error)=>{
        console.log(error)
    })
}

