import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
 
const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [name, setName] = useState('');
    const [select, setSelect] = useState('');
 
    // validation
    const [validationErrors, setValidationErrors] = useState({});
    const [errors, setErrors] = useState({});
 
    const validateForm = () => {
        const errors = {};
 
        if (!name.trim()) {
            errors.name = 'Name is required';
        }
 
        if (!phoneNumber) {
            errors.phoneNumber = 'Phone number is required';
        }
 
        if (!username) {
            errors.username = 'Email / Username is required';
        }
 
        if (!password) {
            errors.password = 'Password is required';
        }
        else if (password.length < 8) {
            errors.password = 'Password should be at least 8 characters long';
        }
        else if (!/(?=.*[a-z])(?=.*[A-Z])/.test(password)) {
            errors.password = 'Password should contain at least one uppercase and one lowercase letter';
        }
        
        if(!confirmPassword){
            errors.confirmPassword = 'Required';
        }
        else if(confirmPassword !== password){
                errors.confirmPassword = 'Password does not match'
        }

        if (!select) {
            errors.select = 'Select Role';
        }
 
        return errors;
    };


 
    const handleSubmit = async (e) => {
        e.preventDefault();
 
        const errors = validateForm();
 
        if (Object.keys(errors).length === 0) {
            try {
                // Send registration data to the backend
                const response = await axios.post('http://localhost:5120/api/User/register', {
                    username,
                    password,
                    confirmPassword,
                    phoneNumber,
                    name
                });
 
                if (response.status === 200) {
                    toast.success('Registration successful');
                    console.log(response.data);
                    setValidationErrors({});
                }
            } catch (error) {
                console.error('Registration failed:', error);
                toast.error('Registration failed');
            }
        } else {
            setValidationErrors(errors);
        }
    };
 
    return (
        <>
            {/* <Navbar />  */}
            <ToastContainer />
