import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Details = () => {
  const [productData, setProductData] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (id) {
          const response = await axios.get(`http://localhost:5120/api/Product/${id}`);
          setProductData(response.data);
        }
      } catch (error) {
        console.error('Error fetching product data:', error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div>
      {productData ? (
        <form method="post">
          <input hidden asp-for="ProductId" />
          <div className="card shadow border-0 mt-4 mb-4">
            <div className="card-header bg-secondary bg-gradient text-light py-4">
              <div className="row">
                <div className="col-12 text-center" key={productData.id}>
                  <h3 className="text-white text-uppercase">{productData.title}</h3>
                  <p className="text-white-50 fw-semibold mb-0">by {productData.author}</p>
                </div>
              </div>
            </div>
            <div className="card-body">
              <div className="py-3">
                <div className="row">
                  <div className="col-6 col-md-2 offset-lg-1 pb-1">
                    <a asp-action="Index" href="/admin" className="btn btn-outline-primary bg-gradient mb-5 fw-semibold btn-sm text-uppercase">
                      <small>Back to home</small>
                    </a>
                  </div>
                </div>
                <div className="row">
                  <div className="col-12 col-lg-3 offset-lg-1 text-center mb-3">
                    <div id="carouselExampleIndicators" className="carousel slide" data-bs-ride="carousel">
                      <div className="carousel-indicators">
                        {productData.images &&
                          productData.images.map((_, index) => (
                            <button
                              key={index}
                              type="button"
                              data-bs-target="#carouselExampleIndicators"
                              data-bs-slide-to={index}
                              aria-current={index === 0 ? 'true' : undefined}
                              aria-label={`Slide ${index + 1}`}
                            ></button>
                          ))}
                      </div>
                      <div className="carousel-inner">
                        {productData.images &&
                          productData.images.map((image, index) => (
                            <div key={index} className={index === 0 ? 'carousel-item active' : 'carousel-item'}>
                              <img src={`http://localhost:5120/${image}`} className="d-block w-100" alt={`Slide ${index + 1}`} />
                            </div>
                          ))}
                      </div>
                      <button
                        className="carousel-control-prev"
                        type="button"
                        data-bs-target="#carouselExampleIndicators"
                        data-bs-slide="prev"
                      >
                        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Previous</span>
                      </button>
                      <button
                        className="carousel-control-next"
                        type="button"
                        data-bs-target="#carouselExampleIndicators"
                        data-bs-slide="next"
                      >
                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Next</span>
                      </button>
                    </div>
                  </div>
                  <div className="col-12 col-lg-6 offset-lg-1">
                    <div className="col-12 col-md-6 pb-4">
                      <span className="badge">@Model.Product.Category.Name</span>
                    </div>
                    <div className="row ps-2">
                      <h6 className="text-dark text-opacity-50 ">ISBN : {productData.isbn}</h6>
                    </div>
                    {/* ... other details ... */}
                    <div className="row pl-2 mb-3">
                      <div className="col-md-4">
                        <div className="input-group mb-3">
                          <span className="input-group-text bg-primary text-white border-0 fw-semibold" id="inputGroup-sizing-default">
                            Count
                          </span>
                          <input asp-for="Count" type="number" value="1" className="form-control text-end" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />
                        </div>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-12 col-md-6 pb-1">
                        <button type="submit" className="btn btn-primary bg-gradient w-100 py-2 text-uppercase fw-semibold">
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Details;
