import Navbar from './Navbar'

 const Home =()=> {

  const [product, setProduct] = useState([]);

  useEffect(() => {
    getData();
  }, []);
 
  const getData = () => {
    axios
      .get('http://localhost:5120/api/Product')
      .then((result) => {
        setProduct(result.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
    <Navbar></Navbar>
    <div className='container my-3'>
    <div className="row">
    { products.map((product) => (
  <div className="column">
    <div className="card1">
    <img src="product.imageUrl" alt="book" style={{height:"170px"}} />
      <h3 className='my-3'>product.title</h3>
      <p>by <span style={{color:"orange"}}>product.author</span></p>
      <p className=" text-dark text-opacity-75 text-center mb-0">List Price:<span className="text-decoration-line-through">₹99</span></p>
      <p>As Low as:₹80</p>
      <button className='btn btn-dark' style={{width:"180px", height:"40px"}}>Details</button>
      
    </div>
  </div>
  ))};
</div></div>
