import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Details = () => {
  const { id } = useParams();
  const [productData, setProductData] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5120/api/Product/${id}`)
      .then((result) => {
        // Update image URLs to contain only the file name
        const fileName = result.data.imageUrl.split('\\').pop(); // Adjust for other platforms if needed
        const modifiedData = { ...result.data, imageUrl: fileName };
        setProductData(modifiedData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  return (
    <div>
      <form method="post">
        <div className="card shadow border-0 mt-4 mb-4">
          <div className="card-header bg-secondary bg-gradient text-light py-4">
            <div className="row">
            {productData.map((product) => (
              <div className="col-12 text-center">
                <h3 className="text-white text-uppercase">{product.title}</h3>
                <p className="text-white-50 fw-semibold mb-0">by {product.author}</p>
              </div>
               ))}
            </div>
          </div>
          <div className="card-body">
            <div className="py-3">
              <div className="row">
                <div className="col-6 col-md-2 offset-lg-1 pb-1">
                  <a href="/admin" className="btn btn-outline-primary bg-gradient mb-5 fw-semibold btn-sm text-uppercase">
                    <small>Back to home</small>
                  </a>
                </div>
              </div>
              <div className="row">
                <div className="col-12 col-lg-3 offset-lg-1 text-center mb-3">
                  <div id="carouselExampleIndicators" className="carousel slide" data-bs-ride="carousel">
                    <div className="carousel-indicators">
                      <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="0" aria-current="true" aria-label="Slide 1"></button>
                    </div>
                    <div className="carousel-inner">
                      <div className="carousel-item active">
                        <img src={productData.imageUrl} className="d-block w-100" alt="Product" />
                      </div>
                    </div>
                    {/* Add your carousel controls here */}
                  </div>
                </div>
                <div className="col-12 col-lg-6 offset-lg-1">
                  {/* Add your product details display here */}
                  <div className="col-12 col-md-6 pb-4">
                    <span className="badge">{productData.category}</span>
                  </div>
                  <div className="row ps-2">
                    <h6 className="text-dark text-opacity-50">ISBN: {productData.isbn}</h6>
                  </div>
                  {/* Add more details based on your data */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Details;
