import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from './CartContext';

const Cart = ({title}) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { addToCart } = useCart();
    const [productData, setProductData] = useState(null);
    const [count, setCount] = useState(1);
    
 
    // useEffect(() => {
    //     const fetchData = async () => {
    //       try {
    //         if (id) {
    //           const response = await axios.get(`http://localhost:5120/api/Product/${id}`);
    //           setProductData(response.data);
    //         }
    //       } catch (error) {
    //         console.error('Error fetching product data:', error);
    //       }
    //     };
 
    //     fetchData();
    //   }, [id]);
 
    useEffect(() => {
        const fetchData1 = async () => {
            try {
                if (id) {
                    const response = await axios.get(`http://localhost:5120/api/Product/${id}`);
                    const productData = response.data;
 
                    // Extract the filename from the "fakepath" and update the imageUrl
                    const imageUrl = productData.imageUrl.split('\\').pop();
                    setProductData({ ...productData, imageUrl });
 
                    // Log the updated image URL
                    console.log('Updated Image URL:', imageUrl);
                }
            } catch (error) {
                console.error('Error fetching product data:', error);
            }
        };
 
        fetchData1();
    }, [id]);
 
 
 
    const handleCountChange = (e) => {
        // Ensure count is a positive integer
        const newCount = Math.max(1, parseInt(e.target.value, 10) || 1);
        setCount(newCount);
    };

    const handleAddToCart = () => {
        if (productData) {
          addToCart({
            id: productData.id,
            product: productData,
            count
          });
          console.log('Item added to cart:', productData); // Add this line
          navigate('/cart');
        }
      };

    return (
            <div className="container">
      <form method="post">
        <br />
        
        <div className="card shadow border-0">
          <div className="card-header bg-secondary bg-gradient text-light ml-0 py-4 text-center">
            <div className="row px-4">
              <div className="col-12">
                <h5 className="pt-2 text-white">Shopping Cart</h5>
              </div>
            </div>
          </div>
          <div className="card-body my-4">
            <div className="row mb-3 pb-3">
              <div className="col-md-2 offset-md-1">
                <a
                  href="/admin"
                  className="btn btn-outline-primary text-uppercase mb-5 btn-sm"
                >
                  <small>Continue Shopping</small>
                </a>
              </div>
              <div className="col-md-10 offset-md-1">
                {cartItems.map((item) => (
                  <div key={item.id} className="row border-bottom pb-3">
                    <div className="d-none d-lg-block col-lg-1 text-center py-2">
                      {/* Add your cart item image rendering logic here */}
                    </div>
                    <div className="col-12 col-lg-6 pt-md-3">
                      <h5 className="text-uppercase text-secondary">
                        <strong>{item.product.title}</strong>
                      </h5>
                      <p>
                        <small>{item.product.description}</small>
                      </p>
                    </div>
                    <div className="col-12 col-lg-5 text-center row">
                      {/* Add your cart item count, price, and buttons rendering logic here */}
                      <div className="col-4">
                        <button
                          type="button"
                          onClick={() => handleMinus(item.id)}
                          className="btn btn-outline-secondary"
                        >
                          -
                        </button>
                      </div>
                      <div className="col-4">{item.count}</div>
                      <div className="col-4">
                        <button
                          type="button"
                          onClick={() => handlePlus(item.id)}
                          className="btn btn-outline-secondary"
                        >
                          +
                        </button>
                      </div>
                      <div className="col-12 mt-2">
                        <button
                          type="button"
                          onClick={() => handleRemove(item.id)}
                          className="btn btn-outline-danger"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="card-footer bg-white border-0">
              <div className="row">
                <div className="col-md-4 offset-md-4">
                  <ul className="list-group mb-4">
                    {cartItems.length > 0 && (
                      <li className="d-flex justify-content-between align-items-center">
                        <h5 className="text-dark fw-semibold text-uppercase">
                          Total (USD)
                        </h5>
                        <h4 className="text-dark fw-bolder">
                          {cartItems
                            .reduce(
                              (total, item) =>
                                total + (item.price * item.count || 0),
                              0
                            )
                            .toLocaleString('en-US', {
                              style: 'currency',
                              currency: 'USD',
                            })}
                        </h4>
                      </li>
                    )}
                  </ul>
                </div>
              </div>
              <div className="row">
                <div className="col-md-2 offset-md-5">
                  <button
                    type="button"
                    onClick={handleSummary}
                    className="btn btn-primary border-0 bg-gradient w-100 py-2"
                  >
                    Summary
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
          );
        };
        
        export default Cart;
