    .then((result)=>{
        setData(result.data)
    })
    .catch((error)=>{
        console.log(error)
    })
}

const handleSave = () => {
    const url = 'http://localhost:5120/api/Category';
    const data = {
       
        "name": categoryName,
        "order": order
      }
      axios.post(url, data)
      .then((result) =>{
        getData();
        clear();
        toast.success('Category has been added');
      })
      .catch((error)=>{
        toast.error(error);
      })
}

const clear = () => {
    setCategoryName('');
    setOrder('');
}

  return (
    <div className='container'>
        <ToastContainer></ToastContainer>
      <div className="card shadow border-0 mt-4">
    <div className="card-header bg-dark bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-light">Category List</h2>
            </div>
        </div>
    </div>

    <div className="card-body p-4">
        <div className="row pb-3">
            <div className="col-6">
            </div>


            <div className="col-6 text-end">
                <a className="btn btn-dark" href='/create' >
                    
                    <FontAwesomeIcon icon={faCirclePlus} className='mx-2' /> Create New Category
                </a>
            </div>
        </div>

        <table className="table table-bordered table-striped">
            <thead>
                <tr>
                    <th>
                        Category Name
                    </th>
                    <th>
                        Display Order
                    </th>
                    <th>Edit/Delete</th>
                </tr>
            </thead>

            <tbody>
               
            {
            data && data.length>0 ?
                data.map((item, index) => {
                    return(
                        <tr key={index}>
                        {/* <td>{index + 1}</td> */}
                        {/* <td>{item.id}</td>  We will not show id to the user */}
                        <td>{item.categoryName}</td>
                        <td>{item.order}</td>
                        {/* <td>{item.isActive}</td> */}
                        <td>
                            <button className="btn btn-primary">Edit</button> &nbsp;
                            <button className="btn btn-danger">Delete</button>
                        </td>
                      </tr>
                    )
                })
                :
                'Loading.....'
        }
                
            </tbody>
        </table>
    </div></div>
    </div>
  )
}

export default Category
