import React, { useState } from 'react';

const Cart = () => {
    // State to manage the cart items
    const [cartItems, setCartItems] = useState([]);

    // Function to handle incrementing the quantity of an item in the cart
    const handlePlus = (cartItemId) => {
        const updatedCartItems = cartItems.map((item) =>
            item.id === cartItemId
                ? { ...item, count: item.count + 1 }
                : item
        );

        setCartItems(updatedCartItems);
    };

    // Function to handle decrementing the quantity of an item in the cart
    const handleMinus = (cartItemId) => {
        const updatedCartItems = cartItems.map((item) =>
            item.id === cartItemId && item.count > 1
                ? { ...item, count: item.count - 1 }
                : item
        );

        setCartItems(updatedCartItems);
    };

    // Function to handle removing an item from the cart
    const handleRemove = (cartItemId) => {
        const updatedCartItems = cartItems.filter(
            (item) => item.id !== cartItemId
        );

        setCartItems(updatedCartItems);
    };

    // Function to handle navigating to the summary page
    const handleSummary = () => {
        // Add your logic for navigating to the summary page
        // For example, you can use React Router: history.push('/summary');
    };

    return (
        <div className='container'>
            <form method="post">
                <br />
                <div className="card shadow border-0">
                    {/* ... (rest of your card structure) */}
                    <div className="card-footer bg-white border-0">
                        <div className="row">
                            <div className="col-md-4 offset-md-4">
                                <ul className="list-group mb-4">
                                    <li className="d-flex justify-content-between align-items-center">
                                        <h5 className="text-dark fw-semibold text-uppercase">
                                            Total (USD)
                                        </h5>
                                        <h4 className="text-dark fw-bolder">
                                            {/* Calculate total based on cartItems */}
                                            {cartItems.reduce(
                                                (total, item) =>
                                                    total +
                                                    item.price * item.count,
                                                0
                                            ).toLocaleString('en-US', {
                                                style: 'currency',
                                                currency: 'USD',
                                            })}
                                        </h4>
                                    </li>
                                </ul>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-2 offset-md-5">
                                <button
                                    type="button"
                                    onClick={handleSummary}
                                    className="btn btn-primary border-0 bg-gradient w-100 py-2"
                                >
                                    Summary
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default Cart;
