import React from 'react';
import { useCart } from './CartContext';

const Cart = () => {
  const { cart: cartItems, setCart: setCartItems } = useCart();
  console.log('Cart Items:', cartItems);

  const handlePlus = (cartItemId) => {
    const updatedCartItems = cartItems.map((item) =>
      item.id === cartItemId ? { ...item, count: item.count + 1 } : item
    );
    setCartItems(updatedCartItems);
  };

  const handleMinus = (cartItemId) => {
    const updatedCartItems = cartItems.map((item) =>
      item.id === cartItemId && item.count > 1 ? { ...item, count: item.count - 1 } : item
    );
    setCartItems(updatedCartItems);
  };

  const handleRemove = (cartItemId) => {
    const updatedCartItems = cartItems.filter((item) => item.id !== cartItemId);
    setCartItems(updatedCartItems);
  };

  const handleSummary = () => {
    // Add your logic for navigating to the summary page
    // For example, you can use React Router: history.push('/summary');
  };

  return (
    <div className="container">
      <form method="post">
        <br />
        <div className="card shadow border-0">
          <div className="card-header bg-secondary bg-gradient text-light ml-0 py-4 text-center">
            <div className="row px-4">
              <div className="col-12">
                <h5 className="pt-2 text-white">Shopping Cart</h5>
              </div>
            </div>
          </div>
          <div className="card-body my-4">
            <div className="row mb-3 pb-3">
              <div className="col-md-2 offset-md-1">
                <a href="/admin" className="btn btn-outline-primary text-uppercase mb-5 btn-sm">
                  <small>Continue Shopping</small>
                </a>
              </div>
              <div className="col-md-10 offset-md-1">
                {cartItems.map((item) => (
                  <div key={item.id} className="row border-bottom pb-3">
                    <div className="col-12 col-lg-6 pt-md-3">
                      <h5 className="text-uppercase text-secondary">
                        <strong>{item.product.title}</strong>
                      </h5>
                      <p>
                        <small>{item.product.description}</small>
                      </p>
                    </div>
                    <div className="col-12 col-lg-5 text-center row">
                      <div className="col-4">
                        <button
                          type="button"
                          onClick={() => handleMinus(item.id)}
                          className="btn btn-outline-secondary"
                        >
                          -
                        </button>
                      </div>
                      <div className="col-4">{item.count}</div>
                      <div className="col-4">
                        <button
                          type="button"
                          onClick={() => handlePlus(item.id)}
                          className="btn btn-outline-secondary"
                        >
                          +
                        </button>
                      </div>
                      <div className="col-12 mt-2">
                        <button
                          type="button"
                          onClick={() => handleRemove(item.id)}
                          className="btn btn-outline-danger"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="card-footer bg-white border-0">
              <div className="row">
                <div className="col-md-4 offset-md-4">
                  <ul className="list-group mb-4">
                    {cartItems.length > 0 && (
                      <li className="d-flex justify-content-between align-items-center">
                        <h5 className="text-dark fw-semibold text-uppercase">Total (USD)</h5>
                        <h4 className="text-dark fw-bolder">
                          {cartItems
                            .reduce((total, item) => total + (item.price * item.count || 0), 0)
                            .toLocaleString('en-US', {
                              style: 'currency',
                              currency: 'USD',
                            })}
                        </h4>
                      </li>
                    )}
                  </ul>
                </div>
              </div>
              <div className="row">
                <div className="col-md-2 offset-md-5">
                  <button
                    type="button"
                    onClick={handleSummary}
                    className="btn btn-primary border-0 bg-gradient w-100 py-2"
                  >
                    Summary
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Cart;
