    // };

    const [editID,setEditId] = useState('')
    const [editName, setEditName] = useState('')
    const [editOrder,setEditOrder] = useState('')

    const [data,setData] = useState([]);

useEffect(() => {
    getData();
}, [])

const getData = () =>
{
    axios.get('http://localhost:5120/api/Category')
    .then((result)=>{
        setData(result.data)
       // toast.success('Category has been added')
    })
    .catch((error)=>{
        console.log(error)
    })
}

   

const handleUpdate = () =>{
    const url= (`http://localhost:5120/api/Category/${editID}`);
    const data = {
        id:editID,
        name: editName,
        number: editOrder,
    }
    axios.put(url, data)
      .then((result) =>{
        // handleClose();
        getData();
        //clear();
        //toast.success('Employee has been updated');
      })
      .catch((error)=>{
        console.error(error);
      })
}




  return (
    <div className='container'>
      <div className="card shadow bnumber-0 mt-4">
    <div className="card-header bg-info bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-white py-2">Update Category</h2>
            </div>
        </div>
    </div> 

    <div className="card-body p-4">
    <form method="post" className="row" onSubmit={handleUpdate}>
    <div className="bnumber p-3">

        <div className="form-floating py-2 col-12">
                    <input type="text"
                                name="name"
                                value={editName}
                                onChange={(e)=> setEditName((prevName)=>e.target.value)}
                                // className={`form-control ${errors.name ? 'is-invalid' : ''}`} 
                                className='form-control'
                                />
                    <label className="ms-2">Category Name</label>
   
                    {/* <span className="text-danger">{errors.name}</span>  */}
        </div>

                
                <div className="form-floating py-2 col-12">
                   
                    <input type="number"
                                name="order" 
                                pattern="[0-9]{2}"
                                value={editOrder}
                                onChange={(e)=> setEditOrder(e.target.value)}
                                // className={`form-control ${errors.number ? 'is-invalid' : ''}`} 
                                className='form-control'
                                 />
                   
                      <label className="ms-2">Displaynumber</label> 
     
                      {/* <span className="text-danger">{errors.number}</span> */}
        </div>

        <div className="row pt-3">
        <div className="text-center">
        <button type="submit" className="btn btn-dark mx-2" style={{width:"150px"}} onClick={handleUpdate}>Update</button>
        <a className="btn btn-secondary bnumber-secondary" href='/category'>
            Back To List?
        </a>
       
        </div>
    </div>
    </div>
</form>
    </div>
</div>
    </div>
  )
};

  export default Edit;
