import React, {useState} from 'react'

const Register = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        password2: '',
        phone:'',
        rememberMe: false,
    });

    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        const inputValue = type === 'checkbox' ? checked : value;
        setFormData({
            ...formData,
            [name]: inputValue,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Client-side validation logic
        const newErrors = {};
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!formData.name) {
            newErrors.name = 'Name is required';
        }
        if (!formData.email || !emailRegex.test(formData.email)) {
            newErrors.email = 'Valid email is required';
        }
        if (!formData.password || !/(?=.*[A-Z])(?=.*\d)(?=.*\W)/.test(formData.password)) {
            newErrors.password =
                'Password must contain at least one uppercase letter, one digit, and one special character';
        }
        if (!formData.password2 || !/(?=.*[A-Z])(?=.*\d)(?=.*\W)/.test(formData.password2)) {
            newErrors.password =
                'Password must match';
        }
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };

  return (
  <div className='container'>  
    <div class="card shadow border-0 mt-4">
    <div class="card-header bg-dark bg-gradient mt-0 py-4">
        <div class="row ">
            <div class="col-12 text-center ">
                <h2 class="py-2 text-light">Register</h2>
            </div>
        </div>
    </div>
    </div>

    <div class="card-body p-4">
     <div class="row pt-3">
     <div class="col-md-12">
         <form id="registerForm" class="row" asp-route-returnUrl="@Model.ReturnUrl" method="post" onSubmit={handleSubmit}>
             <h3 class="border-bottom pb-3 mb-4 text-secondary text-center">Create a new account.</h3>
             <div asp-validation-summary="ModelOnly" class="text-danger" role="alert"></div>
             <div class="form-floating mb-3 col-md-12">
                 <input asp-for="Input.Email" class="form-control" onChange={handleInputChange} autocomplete="username" aria-required="true" placeholder="name@example.com" />
                 <label asp-for="Input.Email" class="ms-2 text-muted">Email</label>
                 <span asp-validation-for="Input.Email" class="text-danger"></span>
             </div>
                 <div class="form-floating mb-3 col-md-6">
                     <input class="form-control" onChange={handleInputChange} placeholder="name@example.com" />
                     <label class="ms-2 text-muted" htmlFor='name'>Name</label>
                     <span asp-validation-for="Input.Name" class="text-danger"></span>
                 </div>
                 <div class="form-floating mb-3 col-md-6">
                     <input asp-for="Input.PhoneNumber" onChange={handleInputChange} class="form-control" placeholder="name@example.com" />
                     <label asp-for="Input.PhoneNumber" class="ms-2 text-muted" htmlFor='phone'>Phone Number</label>
                     <span asp-validation-for="Input.PhoneNumber" class="text-danger"></span>
                 </div>
             <div class="form-floating mb-3 col-md-6">
                 <input asp-for="Input.Password" class="form-control" onChange={handleInputChange} autocomplete="new-password" aria-required="true" placeholder="password" />
                     <label asp-for="Input.Password" class="ms-2 text-muted" htmlFor='password'>Password</label>
                 <span asp-validation-for="Input.Password" class="text-danger"></span>
             </div>
             <div class="form-floating mb-3 col-md-6">
                 <input asp-for="Input.ConfirmPassword" class="form-control" onChange={handleInputChange} autocomplete="new-password" aria-required="true" placeholder="password" />
                     <label asp-for="Input.ConfirmPassword" class="ms-2 text-muted" htmlFor='password2'>Confirm Password</label>
                 <span asp-validation-for="Input.ConfirmPassword" class="text-danger"></span>
             </div>
                 
                 <div class="form-floating mb-3 col-md-6">
                     <input asp-for="Input.StreetAddress" class="form-control" placeholder="name@example.com" />
                     <label asp-for="Input.StreetAddress" class="ms-2 text-muted">Street Address</label>
                     <span asp-validation-for="Input.StreetAddress" class="text-danger"></span>
                 </div>
                  <div class="form-floating mb-3 col-md-6">
                     <input asp-for="Input.City" class="form-control" placeholder="name@example.com" />
                     <label asp-for="Input.City" class="ms-2 text-muted">City</label>
                     <span asp-validation-for="Input.City" class="text-danger"></span>
                 </div>
                 <div class="form-floating mb-3 col-md-6">
                     <input asp-for="Input.State" class="form-control" placeholder="name@example.com" />
                     <label asp-for="Input.State" class="ms-2 text-muted">State</label>
                     <span asp-validation-for="Input.State" class="text-danger"></span>
                 </div>
                 <div class="form-floating mb-3 col-md-6">
                     <input asp-for="Input.PostalCode" class="form-control" placeholder="name@example.com" />
                     <label asp-for="Input.PostalCode" class="ms-2 text-muted">Postal Code</label>
                     <span asp-validation-for="Input.PostalCode" class="text-danger"></span>
                 </div>
                 

             <div class="form-floating mb-3 col-md-6">
                 <select class="form-select">
                     <option>-Select Role-</option>
                 </select>
             </div>
             <div class="col-12">
             <button id="registerSubmit" type="submit" class="w-100 btn btn-dark mb-3">Register</button>
                 </div>
         </form>
    </div></div> </div>
     </div>
  )
};

export default Register;
