useEffect(() => {
    if (id) {
      axios
        .get(`http://localhost:5120/api/Product/${id}`)
        .then((result) => {
          // Check if the response is an array
          if (Array.isArray(result.data)) {
            const modifiedData = result.data.map((productData) => {
              // Check if the imageUrl is a local path
              if (productData.imageUrl && productData.imageUrl.startsWith('C:/fakepath')) {
                // If it's a local path, construct a URL based on your server
                const fileName = productData.imageUrl.split('\\').pop();
                productData.imageUrl = `http://localhost:5120/path/to/images/${fileName}`;
              }
              return productData;
            });
  
            setProductData(modifiedData);
          } else if (typeof result.data === 'object') {
            // Handle the case where the response is an object
            // Assuming the server returns an object with a key like "product" containing the data
            const productData = result.data.product;
            if (productData && productData.imageUrl && productData.imageUrl.startsWith('C:/fakepath')) {
              const fileName = productData.imageUrl.split('\\').pop();
              productData.imageUrl = `http://localhost:5120/path/to/images/${fileName}`;
            }
            setProductData(productData);
          } else {
            // Handle other cases where the response is not an array or object
            console.error('Error: Server response is not an array or object');
          }
        })
        .catch((error) => {
          console.error('Error fetching product data:', error);
        });
    }
  }, [id]);
  
  
  

  return (
    <div>
      {productData ? (
        <form method="post">
          <input hidden asp-for="ProductId" />
          <div className="card shadow border-0 mt-4 mb-4">
            <div className="card-header bg-secondary bg-gradient text-light py-4">
              <div className="row">
                <div className="col-12 text-center" key={productData.id}>
                  <h3 className="text-white text-uppercase">{productData.title}</h3>
                  <p className="text-white-50 fw-semibold mb-0">by {productData.author}</p>
                </div>
              </div>
            </div>
            <div className="card-body">
              <div className="py-3">
                <div className="row">
                  <div className="col-6 col-md-2 offset-lg-1 pb-1">
                    <a
                      asp-action="Index"
                      href="/admin"
                      className="btn btn-outline-primary bg-gradient mb-5 fw-semibold btn-sm text-uppercase"
                    >
                      <small>Back to home</small>
                    </a>
                  </div>
                </div>
                <div className="row">
                  <div className="col-12 col-lg-3 offset-lg-1 text-center mb-3">
                    <div className="" >
                      <img src={productData.imageUrl} className="d-block w-100" alt="..." />
                    </div>
                  </div>
                  <div className="col-12 col-lg-6 offset-lg-1">
                    <div className="col-12 col-md-6 pb-4">
                      <span className="badge">@Model.Product.Category.Name</span>
                    </div>
                    <div className="row ps-2">
                      <h6 className="text-dark text-opacity-50 ">ISBN : {productData.isbn}</h6>
                    </div>
                    <div className="row ps-2">
                      <h6 className="text-dark text-opacity-50  pb-2">
                        List Price:
                        <span className="text-decoration-line-through">
                          {productData.listPrice}
                        </span>
                      </h6>
                    </div>
                    <div className="row text-center ps-2">
                      <div className="p-1 col-3 col-lg-2 bg-white border-bottom">
                        <div className="text-dark text-opacity-50 fw-semibold">Quantity</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white border-bottom">
                        <div className="text-dark text-opacity-50 fw-semibold">1-50</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white border-bottom">
                        <div className="text-dark text-opacity-50 fw-semibold">51-100</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white border-bottom">
                        <div className="text-dark text-opacity-50 fw-semibold">100+</div>
                      </div>
                    </div>
                    <div className="row text-center ps-2">
                      <div className="p-1 col-3 col-lg-2 bg-white text-warning fw-bold">
                        <div>Price</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white text-warning fw-bold">
                        <div>{productData.price}</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white text-warning fw-bold">
                        <div>{productData.price50}</div>
                      </div>
                      <div className="p-1 col-3 col-lg-2 bg-white text-warning fw-bold">
                        <div>{productData.price100}</div>
                      </div>
                    </div>
                    <div className="row pl-2 my-3">
                      <p className="text-secondary lh-sm">{productData.description}</p>
                    </div>
                    <div className="row pl-2 mb-3">
                      <div className="col-md-4">
                        <div className="input-group mb-3">
                          <span
                            className="input-group-text bg-primary text-white border-0 fw-semibold"
                            id="inputGroup-sizing-default"
                          >
                            Count
                          </span>
                          <input
                            asp-for="Count"
                            type="number"
                            value={count}
                            onChange={(e) => setCount(e.target.value)} // Handle count changes
                            className="form-control text-end"
                            aria-label="Sizing example input"
                            aria-describedby="inputGroup-sizing-default"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-12 col-md-6 pb-1">
                        <button
                          type="submit"
                          className="btn btn-primary bg-gradient  w-100 py-2 text-uppercase fw-semibold"
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Details;
