import React,{useState} from 'react'

const Create = () => {
    const [formData, setFormData] = useState({
        name: '',
        Description:'',
        ISBN: '',
        Author: '',
        ListPrice: '',
        Price1:'',
        Price50: '',
        Price100:'',
        select:'',
        Image:''
    });
    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Client-side validation logic
        const newErrors = {};
        // const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const priceRegex = /^\d{10}$/; // Adjust as needed
        // const passwordRegex = /(?=.*[A-Z])(?=.*\d)(?=.*\W)/;
        // const selectRegex= '';
        
        if (!formData.name || !/^[a-zA-Z\s]+$/.test(formData.name)) {
            newErrors.name = 'Title is required';
        }
        if (!formData.Description || !/^[a-zA-Z\s]+$/.test(formData.Description)) {
            newErrors.Description = 'Description is required';
        }
        if (!formData.ISBN || !ISBN.test(formData.ISBN)) {
            newErrors.ISBN = 'ISBN is required';
        }
        if (!formData.Author || !/^[a-zA-Z\s]+$/.test(formData.Author)) {
            newErrors.Author = 'Author name  is required';
        }
        if (!formData.ListPrice || !priceRegex.test(formData.ListPrice)) {
            newErrors.ListPrice = 'List Price is required';
        }
        if (!formData.Price1 || !priceRegex.test(formData.Price1)) {
            newErrors.Price1 = 'List Price is required';
        }
        if (!formData.Price50 || !priceRegex.test(formData.Price50)) {
            newErrors.Price50 = 'List Price is required';
        }
        if (!formData.Price100 || !priceRegex.test(formData.Price100)) {
            newErrors.Price100 = 'List Price is required';
        }
        if (!formData.select) {
            newErrors.select =  'Select the Role';
               
        }
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };







  return (
  <div className='container'>
      <div class="card shadow border-0 my-4">
    <div class="card-header bg-info bg-gradient ml-0 py-3">
        <div class="row">
            <div class="col-12 text-center">
                <h2 class="text-white py-2">Create Product</h2>
            </div>
        </div>
    </div> 

    <div class="card-body p-4">
    <form method="post" class="row" enctype="multipart/form-data" onSubmit={handleSubmit}>
        <input asp-for="Product.Id" hidden />
            <input asp-for="Product.ImageUrl" hidden />

        <div class="row">
            <div class="col-10">
                    <div class="border p-3">

                        {/* <!--  <div asp-validation-summary="All"></div> -->  <!--This is used to show all the validation methods at single location--> */}
                        <div class="form-floating py-2 col-12">
                            <input type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                className={`form-control ${errors.name ? 'is-invalid' : ''}`} />
                            <label  class="ms-2">Title</label>

                          
                            <span className="text-danger">{errors.name}</span> 
                        </div>


                        <div class=" py-2 col-12">
                            <label asp-for="Product.Description" class="ms-2 text-muted">Product Description</label>
                            <textarea type="text"
                                name="name"
                                value={formData.Description}
                                onChange={handleInputChange}
                                className={`form-control ${errors.Description ? 'is-invalid' : ''}`}></textarea>

                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="text"
                                name="name"
                                value={formData.ISBN}
                                onChange={handleInputChange}
                                className={`form-control ${errors.ISBN ? 'is-invalid' : ''}`} />
                            <label asp-for="Product.ISBN" class="ms-2">ISBN</label>

                         
                            <span className="text-danger">{errors.ISBN}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="text"
                                name="author"
                                value={formData.Author}
                                onChange={handleInputChange}
                                className={`form-control ${errors.Author ? 'is-invalid' : ''}`} />
                            <label asp-for="Product.Author" class="ms-2">Author</label>

                         
                            <span className="text-danger">{errors.Author}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="number"
                                name="price"
                                value={formData.ListPrice}
                                onChange={handleInputChange}
                                className={`form-control ${errors.ListPrice ? 'is-invalid' : ''}`} />
                            <label asp-for="Product.ListPrice" class="ms-2">List Price</label>

                          
                            <span className="text-danger">{errors.ListPrice}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="number"
                                name="price"
                                value={formData.Price1}
                                onChange={handleInputChange}
                                className={`form-control ${errors.Price1 ? 'is-invalid' : ''}`}  />
                            <label asp-for="Product.Price" class="ms-2">Price for 1-50</label>

                           
                            <span className="text-danger">{errors.Price1}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="number"
                                name="price"
                                value={formData.Price50}
                                onChange={handleInputChange}
                                className={`form-control ${errors.Price50 ? 'is-invalid' : ''}`}  />
                            <label asp-for="Product.Price50" class="ms-2">Price for 50+</label>

                           
                            <span className="text-danger">{errors.Price50}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="number"
                                name="price"
                                value={formData.Price100}
                                onChange={handleInputChange}
                                className={`form-control ${errors.Price100 ? 'is-invalid' : ''}`}  />
                            <label asp-for="Product.Price100" class="ms-2">Price for 100+</label>

                           
                            <span className="text-danger">{errors.Price100}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            {/* <select asp-for="CategoryId" asp-items="ViewBag.CategoryList" class="form-select border-0 shadow" />  */}
                            <select type="select"
                                name="select"
                                value={formData.select}
                                onChange={handleInputChange}
                                className={`form-control ${errors.select ? 'is-invalid' : ''}`}>
                                <option selected>--Select Category--</option>
                                <option >Action</option>
                                <option >Sci-Fi</option>
                                <option >Spiritual</option>
                            </select>
                            <label asp-for="Product.CategoryId" class="ms-2"></label>

                            
                            <span className="text-danger">{errors.select}</span> 
                        </div>

                        <div class="form-floating py-2 col-12">
                            <input type="file" name="file" class="form-control border-0 shadow" />
                            <label asp-for="Product.ImageUrl" class="ms-2"></label>

                            <span className="text-danger">{errors.Image}</span> 
                        </div>

                        <div class="row pt-3">
                            <div class="text-center">
                
                                    <button type="submit" class="btn btn-dark mx-2" style={{width:"150px"}}>Update</button>
                                
                                <a asp-controller="Product" href='/Product' asp-action="Index" class="btn btn-secondary border-secondary">
                                    Back To List?
                                </a>

                            </div>
                        </div>
                    </div>
            </div>
            <div class="col-2">
                <img src="@Model.Product.ImageUrl" alt='img' width="100%" style={{borderRadius:"5px", border:"1px solid #bbb9b9"}}/>
            </div>
        </div>
    
</form>
    </div>
</div>
</div>
  )
}
export default Create
