import React,{useState} from 'react'

const Create = () => {
    const [formData, setFormData] = useState({
        name: '',
        description:'',
        isbn: '',
        author: '',
        listPrice: '',
        price1:'',
        price50: '',
        price100:'',
        select:'',
        image:''
    });
    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Client-side validation logic
        const newErrors = {};
        // const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        //const ISBNRegex = /^[a-zA-Z\s]+$/;
        const priceRegex = /^\d{3}$/; // Adjust as needed
        // const passwordRegex = /(?=.*[A-Z])(?=.*\d)(?=.*\W)/;
        // const selectRegex= '';
        
        if (!formData.name) {
            newErrors.name = 'Title is required';
        }
        if (!formData.description) {
            newErrors.description = 'Description is required';
        }
        if (!formData.isbn) {
            newErrors.isbn = 'ISBN is required';
        }
        if (!formData.author || !/^[a-zA-Z\s]+$/.test(formData.author)) {
            newErrors.author = 'author name  is required';
        }
        if (!formData.listPrice || !priceRegex.test(formData.listPrice)) {
            newErrors.listPrice = 'List Price is required';
        }
        if (!formData.price1 || !priceRegex.test(formData.price1)) {
            newErrors.price1 = 'List Price is required';
        }
        if (!formData.price50 || !priceRegex.test(formData.price50)) {
            newErrors.price50 = 'List Price is required';
        }
        if (!formData.price100 || !priceRegex.test(formData.price100)) {
            newErrors.price100 = 'List Price is required';
        }
        if (!formData.select) {
            newErrors.select =  'Select the Role';
               
        }
        if (!formData.image) {
            newErrors.image =  'Select the image file';
               
        }
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };







  return (
  <div className='container'>
      <div className="card shadow border-0 my-4">
    <div className="card-header bg-info bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-white py-2">Create Product</h2>
            </div>
        </div>
    </div> 

    <div className="card-body p-4">
    <form method="post" className="row" enctype="multipart/form-data" onSubmit={handleSubmit}>
        <input asp-for="Product.Id" hidden />
            <input asp-for="Product.ImageUrl" hidden />

        <div className="row">
            <div className="col-10">
                    <div className="border p-3">

                        {/* <!--  <div asp-validation-summary="All"></div> -->  <!--This is used to show all the validation methods at single location--> */}
                        <div className="form-floating py-2 col-12">
                            <input type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                className={`form-control ${errors.name ? 'is-invalid' : ''}`} />
                            <label  className="ms-2">Title</label>

                          
                            <span className="text-danger">{errors.name}</span> 
                        </div>


                        <div className=" py-2 col-12">
                            <label  htmlFor="description" className="ms-2 text-muted">Product Description</label>
                            <textarea type="text"
                                name="description"
                                value={formData.description}
                                //id='Discription'
                                onChange={handleInputChange}
                                className={`form-control ${errors.description ? 'is-invalid' : ''}`}></textarea>

                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="text"
                                name="isbn"
                                value={formData.isbn}
                                onChange={handleInputChange}
                                className={`form-control ${errors.isbn ? 'is-invalid' : ''}`} />
                            <label className="ms-2">ISBN</label>

                         
                            <span className="text-danger">{errors.isbn}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="text"
                                name="author"
                                value={formData.author}
                                onChange={handleInputChange}
                                className={`form-control ${errors.author ? 'is-invalid' : ''}`} />
                            <label asp-for="Product.author" className="ms-2">Author</label>

                         
                            <span className="text-danger">{errors.author}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="number"
                                name="listPrice"
                                value={formData.listPrice}
                                onChange={handleInputChange}
                                className={`form-control ${errors.listPrice ? 'is-invalid' : ''}`} />
                            <label className="ms-2">List Price</label>

                          
                            <span className="text-danger">{errors.listPrice}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="number"
                                name="price1"
                                value={formData.price1}
                                onChange={handleInputChange}
                                className={`form-control ${errors.price1 ? 'is-invalid' : ''}`}  />
                            <label className="ms-2">Price for 1-50</label>

                           
                            <span className="text-danger">{errors.price1}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="number"
                                name="price50"
                                value={formData.price50}
                                onChange={handleInputChange}
                                className={`form-control ${errors.price50 ? 'is-invalid' : ''}`}  />
                            <label className="ms-2">Price for 50+</label>

                           
                            <span className="text-danger">{errors.price50}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="number"
                                name="price100"
                                value={formData.price100}
                                onChange={handleInputChange}
                                className={`form-control ${errors.price100 ? 'is-invalid' : ''}`}  />
                            <label className="ms-2">Price for 100+</label>

                           
                            <span className="text-danger">{errors.price100}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            {/* <select asp-for="CategoryId" asp-items="ViewBag.CategoryList" className="form-select border-0 shadow" />  */}
                            <select type="select"
                                name="select"
                                value={formData.select}
                                onChange={handleInputChange}
                                className={`form-control ${errors.select ? 'is-invalid' : ''}`}>
                                <option selected>--Select Category--</option>
                                <option >Action</option>
                                <option >Sci-Fi</option>
                                <option >Spiritual</option>
                            </select>
                            <label asp-for="Product.CategoryId" className="ms-2"></label>

                            
                            <span className="text-danger">{errors.select}</span> 
                        </div>

                        <div className="form-floating py-2 col-12">
                            <input type="file" name="image"
                                value={formData.image}
                                onChange={handleInputChange}
                                className={`form-control ${errors.image ? 'is-invalid' : ''}`}/>
                            <label className="ms-2"></label>

                            <span className="text-danger">{errors.image}</span> 
                        </div>

                        <div className="row pt-3">
                            <div className="text-center">
                
                                    <button type="submit" className="btn btn-dark mx-2" style={{width:"150px"}}>Update</button>
                                
                                <a asp-controller="Product" href='/Product' asp-action="Index" className="btn btn-secondary border-secondary">
                                    Back To List?
                                </a>

                            </div>
                        </div>
                    </div>
            </div>
            <div className="col-2">
                <img src="@Model.Product.ImageUrl" alt='img' width="100%" style={{borderRadius:"5px", border:"1px solid #bbb9b9"}}/>
            </div>
        </div>
    
</form>
    </div>
</div>
</div>
  )
}
export default Create
