import React, {useState, useEffect} from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const Edit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
   
    const [category, setCategory] = useState({
      name: '',
      displayOrder: 0,
    });
   
    useEffect(() => {
      // Fetch category data by categoryId and update state
  axios.get(`http://localhost:5120/api/Category/${id}`)
        .then((response) => {
          setCategory(response.data);
        })
        .catch((error) => {
          console.error('Error fetching category data:', error);
        });
    }, [categoryId]);
   
    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setCategory((prevCategory) => ({ ...prevCategory, [name]: value }));
    };
   
    const handleUpdate = () => {
  const url = `http://localhost:5120/api/Category/${categoryId}`;
      axios.put(url, category)
        .then(() => {
          // Optionally, you can redirect to the category list or perform other actions
          navigate('/category');
        })
        .catch((error) => {
          console.error('Error updating category:', error);
        });
    };
   
    return (
      <div className="container">
        <h2>Edit Category</h2>
        <form>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Category Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
  value={category.name}
              onChange={handleInputChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="displayOrder" className="form-label">Display Order</label>
            <input
              type="number"
              className="form-control"
              id="displayOrder"
              name="displayOrder"
              value={category.displayOrder}
              onChange={handleInputChange}
            />
          </div>
          <button type="button" className="btn btn-primary" onClick={handleUpdate}>
            Update Category
          </button>
        </form>
      </div>
    );
  };

  export default Edit;
