import React,{useState, useEffect, useRef} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCirclePlus, faPenToSquare, faTrash } from '@fortawesome/free-solid-svg-icons'
import axios from "axios";
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate, Link, useLocation } from 'react-router-dom';

const Category = () => {

// const [categoryName, setCategoryName] = useState('')
// const [order,setOrder] = useState('')

const navigate = useNavigate();
const { search } = useLocation();
const queryParams = new URLSearchParams(search);
const successParam = queryParams.get('success');

    const [editID,setEditId] = useState('')
    const [editName, setEditName] = useState('')
    const [editOrder,setEditOrder] = useState('')

 const [data,setData] = useState([]);

// useEffect(() => {
//     getData();
// }, [])

// const getData = () =>
// {
//     axios.get('http://localhost:5120/api/Category')
//     .then((result)=>{
//         setData(result.data)
//          toast.success('Category has been added')
//     })
//     .catch((error)=>{
//         console.log(error)
//     })
// }

const toastShownRef = useRef(false);
 
const getData = () => {
  axios
    .get('http://localhost:5211/api/Villas')
    .then((result) => {
      setData(result.data);
    })
    .catch((error) => {
      console.log(error);
    });
};

useEffect(() => {
    getData();
 
    return () => {
      // Clear the toastShown value when the component is unmounted
      localStorage.removeItem('toastShown');
    };
  }, []); // Run once on component mount
 
  useEffect(() => {
    if (successParam === 'true' && !toastShownRef.current) {
      toast.success('Villa has been added');
      toastShownRef.current = true;
      localStorage.setItem('toastShown', 'true');
    }
  }, [successParam]);



// const handleBackToEdit = () => {
//     navigate('/edit');
//   };

  const handleBackToDelete = () => {
    navigate('/delete');
  };

// const handleSave = () => {
//     const url = 'http://localhost:5120/api/Category';
//     const data = {
       
//         "name": categoryName,
//         "order": order
//       }
//       axios.post(url, data)
//       .then((result) =>{
//         getData();
//         clear();
//         toast.success('Category has been added');
//       })
//       .catch((error)=>{
//         toast.error(error);
//       })
// }

// const clear = () => {
//     setCategoryName('');
//     setOrder('');
// }

// const handleEdit =(id) =>{
//     // alert(id);
//     //handleShow();
//     axios.get(`http://localhost:5120/api/Category/${id}`)
//     .then((result)=>{
//         setEditName(result.data.name);
//         setEditOrder(result.data.displayOrder);
//         setEditId(id);
//         navigate(`/edit/${id}`);
//     })
//     .catch((error)=>{
//         console.log(error);
//     })
// } 

const handleDelete = async (id) => {
    if (window.confirm("Are you sure to delete this category") === true) {
      try {
        await axios.delete(`http://localhost:5120/api/Category/${id}`);
        toast.success("Category has been deleted");
        await getData(); // Wait for data to be updated
      } catch (error) {
        toast.error(error.message);
      }
    }
  };

  return (
    <div className='container'>
        <ToastContainer></ToastContainer>
      <div className="card shadow border-0 mt-4">
    <div className="card-header bg-dark bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-light">Category List</h2>
            </div>
        </div>
    </div>

    <div className="card-body p-4">
        <div className="row pb-3">
            <div className="col-6">
            </div>


            <div className="col-6 text-end">
                <a className="btn btn-dark" href='/create' >
                    
                    <FontAwesomeIcon icon={faCirclePlus} className='mx-2' /> Create New Category
                </a>
            </div>
        </div>

        <table className="table table-bordered table-striped">
            <thead>
                <tr>
                    <th>
                        Category Name
                    </th>
                    <th>
                        Display Order
                    </th>
                    <th>Edit/Delete</th>
                </tr>
            </thead>

            <tbody>
               
            {
            data && data.length>0 ?
                data.map((item, index) => {
                    return(
                        <tr key={index}>
                        {/* <td>{index + 1}</td> */}
                        {/* <td>{item.id}</td>  We will not show id to the user */}
                        <td>{item.name}</td>
                        <td>{item.displayOrder}</td>
                        {/* <td>{item.isActive}</td> */}
                        <td>
                        <Link to={`/edit/${item.id}`} className="btn btn-primary"><FontAwesomeIcon icon={faPenToSquare} className='mx-2' />Edit</Link> &nbsp;
                            {/* <a className="btn btn-primary" href='/edit' onClick={()=>handleEdit(item.id)}>Edit</a> */}
                            {/* <button className="btn btn-primary" onClick={handleBackToEdit}>Edit</button> &nbsp; */}
                            <button className="btn btn-danger" onClick={()=> handleDelete(item.id)}><FontAwesomeIcon icon={faTrash} className='mx-2' />Delete</button>
                        </td>
                      </tr>
                    )
                })
                :
                'Loading.....'
        }
                
            </tbody>
        </table>
    </div></div>
    </div>
  )
}

export default Category
