// Cart.js
import React from 'react';
import { useCart } from './CartContext';

const Cart = () => {
  const { cartState, removeFromCart, updateCartItem } = useCart();

  const handlePlus = (cartItemId) => {
    // Implement the logic to increase item count
    updateCartItem(cartItemId, cartState.cartItems.find(item => item.id === cartItemId).count + 1);
  };

  const handleMinus = (cartItemId) => {
    // Implement the logic to decrease item count
    updateCartItem(cartItemId, Math.max(1, cartState.cartItems.find(item => item.id === cartItemId).count - 1));
  };

  const handleRemove = (cartItemId) => {
    // Implement the logic to remove an item from the cart
    removeFromCart(cartItemId);
  };

  return (
    <div className='container'>
      <form method="post">
        <br />
        <div className="card shadow border-0">
          <div className="card-header bg-secondary bg-gradient text-light ml-0 py-4 text-center">
            <div className="row px-4">
              <div className="col-12">
                <h5 className="pt-2 text-white">Shopping Cart</h5>
              </div>
            </div>
          </div>
          <div className="card-body my-4">
            <div className="row mb-3 pb-3">
              <div className="col-md-2 offset-md-1">
                <a
                  href="/admin"
                  className="btn btn-outline-primary text-uppercase mb-5 btn-sm"
                >
                  <small>Continue Shopping</small>
                </a>
              </div>
              <div className="col-md-10 offset-md-1">
                {cartState.cartItems.map((item) => (
                  <div key={item.id} className="row border-bottom pb-3">
                    <div className="d-none d-lg-block col-lg-1 text-center py-2">
                      {item.product.productImages != null &&
                        item.product.productImages.length > 0 ? (
                        <img
                          src={item.product.productImages[0].imageUrl}
                          alt="Product"
                          className="card-img-top rounded w-100"
                        />
                      ) : (
                        <img
                          src="https://placehold.co/500x600/png"
                          alt="Placeholder"
                          className="card-img-top rounded w-100"
                        />
                      )}
                    </div>
                    <div className="col-12 col-lg-6 pt-md-3">
                      <h5 className="text-uppercase text-secondary">
                        <strong>{item.product.title}</strong>
                      </h5>
                      <p>
                        <small
                          dangerouslySetInnerHTML={{
                            __html: item.product.description,
                          }}
                        ></small>
                      </p>
                    </div>
                    <div className="col-6 col-lg-6 pt-2">
                      <div className="w-75 btn-group" role="group">
                        <button
                          onClick={() => handlePlus(item.id)}
                          className="btn btn-outline-primary bg-gradient py-2"
                        >
                          <i className="bi bi-plus-square"></i>
                        </button>{' '}
                        &nbsp;
                        <button
                          onClick={() => handleMinus(item.id)}
                          className="btn btn-outline-primary bg-gradient py-2"
                        >
                          <i className="bi bi-dash-square"></i>
                        </button>
                      </div>
                    </div>
                    <div className="col-6 col-lg-6 text-right pt-2">
                      <button
                        onClick={() => handleRemove(item.id)}
                        className="btn btn-danger bg-gradient py-2"
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="card-footer bg-white border-0">
              <div className="row">
                <div className="col-md-4 offset-md-4">
                  <ul className="list-group mb-4">
                    <li className="d-flex justify-content-between align-items-center">
                      <h5 className="text-dark fw-semibold text-uppercase">
                        Total (USD)
                      </h5>
                      <h4 className="text-dark fw-bolder">
                        {cartState.cartItems.reduce(
                          (total, item) => total + item.price * item.count,
                          0
                        ).toLocaleString('en-US', {
                          style: 'currency',
                          currency: 'USD',
                        })}
                      </h4>
                    </li>
                  </ul>
                </div>
              </div>
              <div className="row">
                <div className="col-md-2 offset-md-5">
                  <a
                    href="/YourSummaryAction"
                    className="btn btn-primary border-0 bg-gradient w-100 py-2"
                  >
                    Summary
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Cart;
