import React from 'react';
import { useCart } from './CartContext';

const Cart = () => {
  const { cart: cartItems, setCart: setCartItems, selectedProduct } = useCart();

  const handlePlus = (cartItemId) => {
    const updatedCartItems = cartItems.map((item) =>
      item.id === cartItemId ? { ...item, count: item.count + 1 } : item
    );
    setCartItems(updatedCartItems);
  };

  const handleMinus = (cartItemId) => {
    const updatedCartItems = cartItems.map((item) =>
      item.id === cartItemId && item.count > 1
        ? { ...item, count: item.count - 1 }
        : item
    );
    setCartItems(updatedCartItems);
  };

  const handleRemove = (cartItemId) => {
    const updatedCartItems = cartItems.filter(
      (item) => item.id !== cartItemId
    );
    setCartItems(updatedCartItems);
  };

  const handleSummary = () => {
    // Placeholder: Add your logic for navigating to the summary page
    // For example, you can use React Router: history.push('/summary');
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cartItems.map((item) => (
        <div key={item.id}>
          <h3>{item.product.title}</h3>
          <p>{item.product.description}</p>
          <p>Quantity: {item.count}</p>
          <button onClick={() => handleMinus(item.id)}>-</button>
          <button onClick={() => handlePlus(item.id)}>+</button>
          <button onClick={() => handleRemove(item.id)}>Remove</button>
        </div>
      ))}
      <button onClick={handleSummary}>Go to Summary</button>
    </div>
  );
};

export default Cart;
