import React from 'react';
import { useCart } from './CartContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faMinus, faTrash } from '@fortawesome/free-solid-svg-icons';

const Cart = () => {
  const { cartItems, handlePlus, handleMinus, removeFromCart, updateQuantityBackend } = useCart();

  const logCartState = () => {
    console.log('Cart state:', cartItems);
  };

  const renderCartItems = () => {
    if (!Array.isArray(cartItems)) {
      return null; // Handle the case where cartItems is not an array
    }

    return cartItems.map((item) => (
      <div key={item.id} className="row border-bottom pb-3">
        <div className="d-none d-lg-block col-lg-1 text-center py-2">
          <img src={item.product.imageUrl} className="card-img-top rounded w-100" alt="Product" />
        </div>
        <div className="col-12 col-lg-6 pt-md-3">
          <h5 className="text-uppercase text-secondary">
            <strong>{item.product.title}</strong>
          </h5>
          <p>
            <small dangerouslySetInnerHTML={{ __html: item.product.description }}></small>
          </p>
        </div>
        <div className="col-12 col-lg-5 text-center row">
          <div className="col-3 text-md-right pt-2 pt-md-4">
            <h6 className="fw-semibold">
              {(item.product && item.product.price !== undefined ? `₹${item.product.price}` : 'N/A')}
              <span className="text-muted">&nbsp;x&nbsp;</span>
              {(item.count !== undefined ? item.count : 'N/A')}
            </h6>
          </div>
          <div className="col-6 col-sm-4 col-lg-6 pt-2">
            <div className="w-75 btn-group" role="group">
              <button onClick={() => handlePlus(item.id)} className="btn btn-outline-primary bg-gradient py-2">
                <FontAwesomeIcon icon={faPlus} />
              </button>{' '}
              &nbsp;
              <button onClick={() => handleMinus(item.id)} className="btn btn-outline-primary bg-gradient py-2">
                <FontAwesomeIcon icon={faMinus} />
              </button>
            </div>
          </div>
          <div className="col-3 col-sm-4 col-lg-2 offset-lg-1 text-right pt-2">
            <button onClick={() => removeFromCart(item.id)} className="btn btn-danger bg-gradient py-2">
              <FontAwesomeIcon icon={faTrash} />
            </button>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className="container">
      <br />
      <div className="card shadow border-0">
        <div className="card-header bg-secondary bg-gradient text-light ml-0 py-4 text-center">
          <div className="row px-4">
            <div className="col-12">
              <h5 className="pt-2 text-white">Shopping Cart</h5>
            </div>
          </div>
        </div>
        <div className="card-body my-4">
          <div className="row mb-3 pb-3">
            <div className="col-md-2 offset-md-1">
              <a href="/admin" className="btn btn-outline-primary text-uppercase mb-5 btn-sm">
                <small>Continue Shopping</small>
              </a>
            </div>
            <div className="col-md-10 offset-md-1">{renderCartItems()}</div>
          </div>
          <div className="card-footer bg-white border-0">
            <div className="row">
              <div className="col-md-4 offset-md-4">
                <ul className="list-group mb-4">
                  <li className="d-flex justify-content-between align-items-center">
                    <h5 className="text-dark fw-semibold text-uppercase">Total (₹)</h5>
                    <h4 className="text-dark fw-bolder">
                      {`₹${cartItems.reduce(
                        (total, item) =>
                          total + (item.product.price !== undefined && item.count !== undefined ? item.product.price * item.count : 0),
                        0
                      )}`}
                    </h4>
                  </li>
                </ul>
              </div>
            </div>
            <div className="row">
              <div className="col-md-2 offset-md-5">
                <a href="/YourSummaryAction" className="btn btn-primary border-0 bg-gradient w-100 py-2">
                  Summary
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;



