import React, {useState} from 'react'

const Create = () => {
    const [formData, setFormData] = useState({
        name: '',
        order:''
    });
    const [errors, setErrors] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const newErrors = {};

        if (!formData.name || !/^[a-zA-Z\s]+$/.test(formData.name)) {
            newErrors.name = 'Valid name is required';
        }

        if (!formData.order || !order.test(formData.phoneNumber)) {
            newErrors.order = 'Order quantity is required';

            if(order>100)
            {
                newErrors.order = 'Order quantity cannot be greater than 100';
            }
            else{
                newErrors.order = '';
            }

        }
        if (Object.keys(newErrors).length === 0) {
            // If no errors, you can proceed with form submission or further processing
            console.log('Form data submitted:', formData);
        } else {
            // If there are errors, update the state to display them
            setErrors(newErrors);
        }
    };

  return (
    <div className='container'>
      <div className="card shadow border-0 mt-4">
    <div className="card-header bg-info bg-gradient ml-0 py-3">
        <div className="row">
            <div className="col-12 text-center">
                <h2 className="text-white py-2">Create Category</h2>
            </div>
        </div>
    </div> 

    <div className="card-body p-4">
    <form method="post" className="row">
    <div className="border p-3">

        <div className="form-floating py-2 col-12">
                    <input type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                className={`form-control ${errors.name ? 'is-invalid' : ''}`} />
                    <label className="ms-2">Category Name</label>
   
                    <span className="text-danger">{errors.name}</span> 
        </div>

                
                <div className="form-floating py-2 col-12">
                   
                    <input type="number"
                                name="order"
                                value={formData.order}
                                onChange={handleInputChange}
                                className={`form-control ${errors.order ? 'is-invalid' : ''}`} />
                   
                      <label className="ms-2">DisplayOrder</label> 
     
                      <span className="text-danger">{errors.order}</span>
        </div>

        <div className="row pt-3">
        <div className="text-center">
        <button type="submit" className="btn btn-dark mx-2" style={{width:"150px"}}>Create</button>
        <a className="btn btn-secondary border-secondary" href='/category'>
            Back To List?
        </a>
       
        </div>
    </div>
    </div>
</form>
    </div>
</div>
    </div>
  )
};

  export default Create;
