import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Details = () => {
    const { id } = useParams(); // Extract product ID from URL
    const [productData, setProductData] = useState([]);

    useEffect(() => {
        axios
            .get(`http://localhost:5120/api/Product/${id}`)
            .then((result) => {
                // Update image URLs to contain only the file name
                const modifiedData = result.data.map((product) => {
                    const fileName = product.imageUrl.split('\\').pop(); // Adjust for other platforms if needed
                    return { ...product, imageUrl: fileName };
                });

                setProductData(modifiedData);
            })
            .catch((error) => {
                console.log(error);
            });
    }, [id]); // Include id in the dependency array to fetch data when id changes

    return (
        <div>
            <form method="post">
                <div className="card shadow border-0 mt-4 mb-4">
                    <div className="card-header bg-secondary bg-gradient text-light py-4">
                        <div className="row">
                            <div className="col-12 text-center">
                                {productData.map((product) => (
                                    <React.Fragment key={product.id}>
                                        <h3 className="text-white text-uppercase">{product.title}</h3>
                                        <p className="text-white-50 fw-semibold mb-0">by {product.author}</p>
                                    </React.Fragment>
                                ))}
                            </div>
                        </div>
                    </div>
                    <div className="card-body">
                        {/* Add your UI elements to display product details */}
                        {/* Example: */}
                        {productData.map((product) => (
                            <React.Fragment key={product.id}>
                                <div className="row">
                                    <div className="col-12 col-lg-3 offset-lg-1 text-center mb-3">
                                        {/* Add your carousel or image display component here */}
                                    </div>
                                    <div className="col-12 col-lg-6 offset-lg-1">
                                        {/* Add your product details display here */}
                                        <div className="col-12 col-md-6 pb-4">
                                            <span className="badge">{product.category}</span>
                                        </div>
                                        <div className="row ps-2">
                                            <h6 className="text-dark text-opacity-50 ">ISBN: {product.isbn}</h6>
                                        </div>
                                        {/* Add more details based on your data */}
                                    </div>
                                </div>
                            </React.Fragment>
                        ))}

                        {/* Add your additional UI elements and form fields as needed */}
                    </div>
                </div>
            </form>
        </div>
    );
};

export default Details;
